import React, { useState } from "react";
import * as FaIcons from "react-icons/fa";
import * as AiIcons from "react-icons/ai";
import { IconContext } from "react-icons";
import { Link } from "react-router-dom";
import { SidebarData } from "./SlidebarData";
import "../style/Navbar.css";
import { useTranslation } from 'react-i18next';

export default function Navbar({setTheme}) {
  const { t } = useTranslation()
  const [sidebar, setSidebar] = useState(false);
  const [theme, settheme] = useState("light")

  const showSidebar = () => setSidebar(!sidebar);

  const isChangeTheme = (event) => {
    const value = event.target.value
    settheme(value)
    setTheme(value)
  }
  return (
    <>
      <IconContext.Provider value={{ color: "#FFF" }}>
        <div className="navbar">
          <Link to="#" className="menu-bars">
            <FaIcons.FaBars onClick={showSidebar} />
          </Link>
          <select value={theme}  onChange={(event) => isChangeTheme(event)} required>
            <option value="light">{t('content.LIGHT')}</option>
            <option value="dark">{t('content.DARK')}</option>
            <option value="blue">{t('content.BLUE')}</option>
          </select>
        </div>

        <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
          <ul className="nav-menu-items" onClick={showSidebar}>
            <li className="navbar-toggle">
              <Link to="#" className="menu-bars">
                <AiIcons.AiOutlineClose className="nav-item" />
              </Link>
              <div style={{ fontSize: 20, color: "white", marginLeft: 10, textDecoration: "none" }}>{t('content.HOME_ADMIN')}</div>
            </li>

            {SidebarData.map((item, index) => {
              return (
                <li key={index} className={item.cName}>
                  <Link to={item.path}>
                    {item.icon}
                    <span>{item.title}</span>
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>
      </IconContext.Provider>
    </>
  );
}
