import React, { useState, useEffect } from "react";
import "./styles.css";
import Navbar from "./components/Navbar";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Home from "./pages/Home";
import Products from "./pages/Products";
import Reports from "./pages/Reports";
import Team from "./pages/Team";
import Message from "./pages/Message";
import Support from "./pages/Support";
import styled, { ThemeProvider } from "styled-components";
import { lightTheme, darkTheme, blueTheme, GlobalStyles } from "./theme";

const StyledApp = styled.div`
  color: ${(props) => props.theme.fontColor};
`;

export default function App() {

  const [theme, setTheme] = useState("light");
  const [searchText, setsearchText] = useState("")
  const [lang, setlang] = useState("")

  useEffect(() => {
    if (localStorage.getItem('lang') === null) {
      localStorage.setItem('lang', JSON.stringify("vn"))
    }
    else {
      let lang = JSON.parse(localStorage.getItem('lang'));
      setlang(lang)
    }
    return () => {

    }
  }, [])
  const themeToggler = () => {
    if(theme === "light") return lightTheme
    else if(theme === "dark") return darkTheme
    else return blueTheme
  };

  return (
    <div>
      <ThemeProvider theme={themeToggler}>
      <GlobalStyles />
      <StyledApp>
      <Router>
        <Navbar setsearchText={(value) => setsearchText(value)} setTheme= {(value)=> setTheme(value)}/>
        <Switch>
          <Route path="/" exact component={Home} />
          <Route path="/products" component={Products} />
          <Route path="/reports" component={Reports} />
          <Route path="/team" component={Team} />
          <Route path="/message" component={Message} />
          <Route path="/support" component={Support} />
        </Switch>
      </Router>
      </StyledApp>
    </ThemeProvider>
    </div>
  );
}
