import React, { useState } from 'react'
import i18n from '../translation/i18n';
import { useTranslation } from 'react-i18next';
import "../style/RightButton.css";

const Header = ({ setTheme }) => {
    const { t } = useTranslation()
    const [theme, settheme] = useState("light")

    function changeLanguage(e) {
        i18n.changeLanguage(e.target.value);
    }

    const isChangeTheme = (event) => {
        const value = event.target.value
        settheme(value)
        setTheme(value)
    }

    return (
        <header>
            <div >
                <div className="dropdown">
                    <select name="" className="dropdown-select" onChange={changeLanguage}>
                        <option value="vi">
                            {t("content.VIETNAMESE")}
                        </option>
                        <option value="en">
                            {t("content.ENGLISH")}
                        </option>
                        <option value="jav">
                            {t("content.JAPANESE")}
                        </option>
                    </select>
                </div>
                <select value={theme} onChange={(event) => isChangeTheme(event)} required>
                    <option value="light">{t('content.LIGHT')}</option>
                    <option value="dark">{t('content.DARK')}</option>
                    <option value="blue">{t('content.BLUE')}</option>
                </select>
            </div>
        </header>
    )
}

export default Header
